package ch.ethz.ssh2.crypto.dh;

import ch.ethz.ssh2.crypto.digest.HashForSSH2Types;
import ch.ethz.ssh2.log.Logger;
import ch.ethz.ssh2.util.StringEncoder;
import java.math.BigInteger;
import java.security.SecureRandom;

public class DhExchange {
   private static final Logger log = Logger.getLogger(DhExchange.class);
   static final BigInteger p1;
   static final BigInteger p14;
   static final BigInteger p16;
   static final BigInteger p18;
   static final BigInteger g;
   BigInteger p;
   BigInteger e;
   BigInteger x;
   BigInteger f;
   BigInteger y;
   BigInteger k;
   private String hashFunction = "SHA1";

   static {
      String p1_string = "";
      String p14_string = "";
      String p16_string = "";
      String p18_string = "";
      p1 = new BigInteger("");
      p14 = new BigInteger("", 16);
      p16 = new BigInteger("", 16);
      p18 = new BigInteger("", 16);
      g = new BigInteger("2");
   }

   public DhExchange(String hashFunction) {
      this.hashFunction = hashFunction;
   }

   public void clientInit(int group, SecureRandom rnd) {
      this.k = null;
      if (group == 1) {
         this.p = p1;
      } else if (group == 14) {
         this.p = p14;
      } else if (group == 16) {
         this.p = p16;
      } else {
         if (group != 18) {
            throw new IllegalArgumentException("Unknown DH group " + group);
         }

         this.p = p18;
      }

      do {
         this.x = new BigInteger(this.p.bitLength() - 1, rnd);
      } while(this.x.compareTo(BigInteger.ONE) <= 0);

      this.e = g.modPow(this.x, this.p);
   }

   public void serverInit(int group, SecureRandom rnd) {
      this.k = null;
      if (group == 1) {
         this.p = p1;
      } else {
         if (group != 14) {
            throw new IllegalArgumentException("Unknown DH group " + group);
         }

         this.p = p14;
      }

      this.y = new BigInteger(this.p.bitLength() - 1, rnd);
      this.f = g.modPow(this.y, this.p);
   }

   public BigInteger getE() {
      if (this.e == null) {
         throw new IllegalStateException("DhDsaExchange not initialized!");
      } else {
         return this.e;
      }
   }

   public BigInteger getF() {
      if (this.f == null) {
         throw new IllegalStateException("DhDsaExchange not initialized!");
      } else {
         return this.f;
      }
   }

   public BigInteger getK() {
      if (this.k == null) {
         throw new IllegalStateException("Shared secret not yet known, need f first!");
      } else {
         return this.k;
      }
   }

   public void setF(BigInteger f) {
      if (this.e == null) {
         throw new IllegalStateException("DhDsaExchange not initialized!");
      } else if (BigInteger.ZERO.compareTo(f) < 0 && this.p.compareTo(f) > 0) {
         this.f = f;
         this.k = f.modPow(this.x, this.p);
      } else {
         throw new IllegalArgumentException("Invalid f specified!");
      }
   }

   public void setE(BigInteger e) {
      if (this.f == null) {
         throw new IllegalStateException("DhDsaExchange not initialized!");
      } else if (BigInteger.ZERO.compareTo(e) < 0 && this.p.compareTo(e) > 0) {
         this.e = e;
         this.k = e.modPow(this.y, this.p);
      } else {
         throw new IllegalArgumentException("Invalid e specified!");
      }
   }

   public byte[] calculateH(byte[] clientversion, byte[] serverversion, byte[] clientKexPayload, byte[] serverKexPayload, byte[] hostKey) {
      HashForSSH2Types hash = new HashForSSH2Types(this.hashFunction);
      if (log.isInfoEnabled()) {
         log.info("Client: '" + StringEncoder.GetString(clientversion) + "'");
         log.info("Server: '" + StringEncoder.GetString(serverversion) + "'");
      }

      hash.updateByteString(clientversion);
      hash.updateByteString(serverversion);
      hash.updateByteString(clientKexPayload);
      hash.updateByteString(serverKexPayload);
      hash.updateByteString(hostKey);
      hash.updateBigInt(this.e);
      hash.updateBigInt(this.f);
      hash.updateBigInt(this.k);
      return hash.getDigest();
   }

   public String getHashFunction() {
      return this.hashFunction;
   }
}
